package array;import java.util.ArrayList;import java.util.Collections;import java.util.List;public class LeadersOfAnArray {    private static List<Integer> leaders(int[] arr) {        int n = arr.length;        int leader = arr[n-1];        List<Integer> res = new ArrayList<>();        res.add(leader);        for(int i = n - 2; i >= 0; i--){            if(arr[i] > leader){                leader = arr[i];                res.add(leader);            }        }        return res;    }    public static void main(String[] args) {        int arr[] = new int[]{6, 5, 2, 0, 0, 4, 1};        List<Integer> res = leaders(arr);        Collections.reverse(res);        res.forEach(i -> System.out.print(i + " "));    }}