package linkedlist;class ListNode{    int data;    ListNode next;    public ListNode(int data){        this.data = data;    }}public class AddTwoSortedList {        private static void displayList(ListNode head) {            ListNode ptr = head;            while(ptr != null){                System.out.print(ptr.data +" ");                ptr = ptr.next;            }        }        public static ListNode addListNode(ListNode node1, ListNode node2) {            ListNode dummy = new ListNode(-1);            ListNode ptr = dummy;            while(node1 != null && node2 != null){                if(node1.data < node2.data){                    ptr.next = node1;                    ptr = ptr.next;                    node1 = node1.next;                }                else {                    ptr.next = node2;                    ptr = ptr.next;                    node2 = node2.next;                }            }            if(node1 != null)                ptr.next = node1;            else                ptr.next = node2;            return dummy.next;        }        public static void main(String[] args) {            ListNode node1 = new ListNode(1);            node1.next = new ListNode(3);            node1.next.next = new ListNode(5);            ListNode node2 = new ListNode(2);            node2.next = new ListNode(4);            node2.next.next = new ListNode(6);            node2.next.next.next = new ListNode(8);            ListNode res = addListNode(node1, node2);            displayList(res);    }}